import React, { Component } from 'react';
import * as d3 from "d3";

class Timeline extends Component {
  constructor() {
    super()
    this.state = {
    }
    this.drawChart = this.drawChart.bind(this)
    this.formatValue = this.formatValue.bind(this)
    this.formateDate = this.formateDate.bind(this)

  }

  componentDidMount() {
    this.drawChart()
  }

  formateDate (date) {
    return date.toLocaleString("en", {
        month: "short",
        day: "numeric",
        year: "numeric",
        timeZone: "UTC"
      });
  }

    formatValue(value) {
    return value.toLocaleString("en", {
        style: "currency",
        currency: "USD"
      });    
  }

  async drawChart() {
    var data = Object.assign(d3.csvParse('../data/aapl.csv').map(({date, close}) => ({date, value: close})), {y: "$ Close"})      
    var svg = d3.select("body").append("svg")
      .attr("width", 960)
      .attr("height", 300)

    var margin = ({top: 20, right: 30, bottom: 30, left: 40})
    var width = svg.attr("width") - margin.left - margin.right;
    var height = svg.attr("height") - margin.bottom - margin.top;


    var x = d3.scaleUtc()
    .domain(d3.extent(data, d => d.date))
    .range([margin.left, width - margin.right])

    var y = d3.scaleLinear()
    .domain([0, d3.max(data, d => d.value)]).nice()
    .range([height - margin.bottom, margin.top])

    var xAxis = g => g
        .attr("transform", `translate(0,${height - margin.bottom})`)
        .call(d3.axisBottom(x).ticks(width / 80).tickSizeOuter(0))

    var yAxis = g => g
        .attr("transform", `translate(${margin.left},0)`)
        .call(d3.axisLeft(y))
        .call(g => g.select(".domain").remove())
        .call(g => g.select(".tick:last-of-type text").clone()
            .attr("x", 3)
            .attr("text-anchor", "start")
            .attr("font-weight", "bold")
            .text(data.y))

    var line = d3.line()
        .curve(d3.curveStep)
        .defined(d => !isNaN(d.value))
        .x(d => x(d.date))
        .y(d => y(d.value))

    svg.append("g")
    .call(xAxis);

    svg.append("g")
        .call(yAxis);

    svg.append("path")
        .datum(data)
        .attr("fill", "none")
        .attr("stroke", "steelblue")
        .attr("stroke-width", 1.5)
        .attr("stroke-linejoin", "round")
        .attr("stroke-linecap", "round")
        .attr("d", line);

        const tooltip = svg.append("g");

        svg.on("touchmove mousemove", function() {
          const {date, value} = bisect(d3.mouse(this)[0]);

          tooltip
              .attr("transform", `translate(${x(date)},${y(value)})`)
              .call(this.callout, `${this.formatValue(value)}
              ${this.formatDate(date)}`);
        });

        svg.on("touchend mouseleave", () => tooltip.call(this.callout, null));

    function bisect() {
        const bisect = d3.bisector(d => d.date).left;
        return mx => {
          const date = x.invert(mx);
          const index = bisect(data, date, 1);
          const a = data[index - 1];
          const b = data[index];
          return b && (date - a.date > b.date - date) ? b : a;
        };
      }

  }


  render(){
    return (<div>{this.drawChart()}</div>)
  }
}


export default Timeline;
