import React, { Component } from 'react';
import * as d3 from "d3";

class Timeline extends Component {
  constructor() {
    super()
    this.state = {
    }
    this.drawChart = this.drawChart.bind(this)
  }

  componentDidMount() {
    this.drawChart()
  }


  async drawChart() {
    var data = data = Object.assign(d3.csvParse(await FileAttachment("aapl.csv").text(), d3.autoType).map(({date, close}) => ({date, value: close})), {y: "$ Close"})      
    var svg = d3.select("body").append("svg")
      .attr("width", 960)
      .attr("height", 300)

    var margin = ({top: 20, right: 30, bottom: 30, left: 40})
    var width = svg.attr("width") - margin.left - margin.right;
    var height = svg.attr("height") - margin.bottom - margin.top;


    var x = d3.scaleUtc()
    .domain(d3.extent(data, d => d.date))
    .range([margin.left, width - margin.right])

    var y = d3.scaleLinear()
    .domain([0, d3.max(data, d => d.value)]).nice()
    .range([height - margin.bottom, margin.top])

    var xAxis = g => g
        .attr("transform", `translate(0,${height - margin.bottom})`)
        .call(d3.axisBottom(x).ticks(width / 80).tickSizeOuter(0))

    var yAxis = g => g
        .attr("transform", `translate(${margin.left},0)`)
        .call(d3.axisLeft(y))
        .call(g => g.select(".domain").remove())
        .call(g => g.select(".tick:last-of-type text").clone()
            .attr("x", 3)
            .attr("text-anchor", "start")
            .attr("font-weight", "bold")
            .text(data.y))

    x.domain(d3.extent(stockPlotData, function(d) { return parseTime(d.date); }));
    y.domain([0, 
              d3.max(stockPlotData, function(d) { 
                return d.a;
              })]);

    var multiline = function(category) {
      var line = d3.line()
                  .x(function(d) { return x(parseTime(d.date)); })
                  .y(function(d) { return y(d[category]); });
      return line;
    }

    var categories = ['a'];
    //var color = d3.scaleOrdinal(d3.schemeCategory10);

    var g = svg.append("g")
        .attr("transform",
          "translate(" + margin.left + "," + margin.top + ")");

    for (let i in categories) {
      var lineFunction = multiline(categories[i]);
      g.append("path")
        .datum(stockPlotData) 
        .attr("class", "line")
        .style("stroke", "blue")
        .style("fill", "None")
        .attr("d", lineFunction);
    }

    // Add the X Axis
    g.append("g")
    .attr("transform", "translate(0," + height + ")")
    .call(d3.axisBottom(x).tickFormat(d3.timeFormat("%Y-%m-%d")));

    // Add the Y Axis
    g.append("g")
    .call(d3.axisLeft(y));

    function formateDate () {
        return date.toLocaleString("en", {
            month: "short",
            day: "numeric",
            year: "numeric",
            timeZone: "UTC"
          });
      }

    function formatValue() {
        return value.toLocaleString("en", {
            style: "currency",
            currency: "USD"
          });    
      }

    function bisect() {
        const bisect = d3.bisector(d => d.date).left;
        return mx => {
          const date = x.invert(mx);
          const index = bisect(data, date, 1);
          const a = data[index - 1];
          const b = data[index];
          return b && (date - a.date > b.date - date) ? b : a;
        };
      }

  }


  render(){
    return (<div>{this.state.ticker}</div>)
  }
}


export default Timeline;
